mapper.select:
SELECT 
FROM edu_class c
LEFT JOIN sys_user u ON u.user_id = c.teacher_id
LEFT JOIN edu_major m ON m.major_id = c.major_id

  AND c.class_name LIKE concat('%', #{className}, '%')
  AND c.grade = #{grade}
  AND c.major_id = #{majorId}
  AND c.teacher_id = #{teacherId}
  AND c.status = #{status}

ORDER BY c.grade DESC, m.major_name ASC, c.class_name ASC

mapper.update:
UPDATE edu_class
SET class_name=#{className}, grade=#{grade}, major_id=#{majorId}, teacher_id=#{teacherId}, status=#{status}, update_by=#{updateBy}, update_time=#{updateTime}
WHERE class_id = #{classId}
