mapper.select:
SELECT  FROM edu_class

  AND class_name LIKE concat('%', #{className}, '%')
  AND grade = #{grade}
  AND major = #{major}
  AND status = #{status}

ORDER BY grade DESC, major ASC, class_name ASC

mapper.update:
UPDATE edu_class
SET class_name=#{className}, grade=#{grade}, major=#{major}, teacher_id=#{teacherId}, class_desc=#{classDesc}, status=#{status}, update_by=#{updateBy}, update_time=#{updateTime}
WHERE class_id = #{classId}
