mapper.select:
SELECT , GROUP_CONCAT(u.nick_name) AS teacher_names, (SELECT COUNT(*) FROM edu_course_student cs WHERE cs.course_id = c.course_id) AS student_count FROM edu_course c LEFT JOIN edu_course_teacher ct ON c.course_id = ct.course_id LEFT JOIN sys_user u ON ct.teacher_id = u.user_id LEFT JOIN edu_major m ON m.major_id = c.major_id
AND c.course_name LIKE concat('%', #{courseName}, '%') AND c.major_id = #{majorId} AND c.status = #{status} AND ct.teacher_id = #{teacherId}
GROUP BY c.course_id ORDER BY c.create_time DESC

mapper.update:
UPDATE edu_course
SET course_name=#{courseName}, major_id=#{majorId}, course_desc=#{courseDesc}, status=#{status}, course_img=#{courseImg}, lesson_hours=#{lessonHours}, update_time=#{updateTime}, approval_required=#{approvalRequired}
WHERE course_id = #{courseId}
