mapper.select:
SELECT 
FROM edu_homework_submit
WHERE course_id = #{courseId}
AND user_id = #{userId}
ORDER BY submit_time DESC

SELECT 
FROM edu_homework_submit
WHERE homework_id = #{homeworkId}
AND user_id = #{userId}
LIMIT 1

mapper.update:
UPDATE edu_homework_submit
SET submit_content = #{submitContent},
    student_name = #{studentName},
    submit_time = NOW(),
    submit_status = #{submitStatus}
WHERE submit_id = #{submitId}
