mapper.select:



        AND r.resource_type = #{resourceType}


        AND r.resource_id = #{resourceId}


        AND r.knowledge_id = #{knowledgeId}


        AND k.knowledge_name like concat('%', #{knowledgeName}, '%')


        AND (
        (r.resource_type = 'homework' and h.homework_name like concat('%', #{resourceName}, '%')) OR
        (r.resource_type = 'question' and q.question_title like concat('%', #{resourceName}, '%')) OR
        (r.resource_type = 'experiment' and e.experiment_name like concat('%', #{resourceName}, '%'))
        )


        AND k.course_id = #{courseId}


order by r.create_time desc

mapper.update:
update sys_teaching_resource_knowledge
resource_type = #{resourceType}, resource_id = #{resourceId}, knowledge_id = #{knowledgeId},
where id = #{id}
